 GLfloat x420=0.033; GLfloat y420= -0.088; GLfloat radius420 =0.021;
	int triangleAmount420 = 20; //# of triangles used to draw circle

	//GLfloat radius = 0.8f; //radius
	GLfloat twicePi420 = 2.0f * PI;

	glBegin(GL_TRIANGLE_FAN);
	glColor3ub(230, 0, 0); // sun
		glVertex2f(x420, y420); // center of circle
		for(r = 0; r <= triangleAmount420;r++) {
			glVertex2f(
		            x420 + (radius420* cos(r *  twicePi420 / triangleAmount420)),
			    y420 + (radius420 * sin(r * twicePi420 / triangleAmount420))
			);
		}
	glEnd();




